import React from 'react';
import { Link } from 'react-router-dom';

function Home() {
    const scrollToBlog = () => {
        const blogSection = document.getElementById('blog-section');
        if (blogSection) {
            blogSection.scrollIntoView({ behavior: 'smooth' });
        }
    };

    return (
        <div className="home">
            <div className="home-content">
                <h1>Market Trend Predictor</h1>
                <p>Discover the latest market trends and insights</p>
                <Link to="/trends">
                    <button className="explore-button">Explore Trends</button>
                </Link>
                <button className="scroll-button" onClick={scrollToBlog}>Read Market Research Blog</button>
            </div>
            <div id="blog-section" className="blog-section">
                <h2>Market Research Blog</h2>
                <article className="blog-post">
                    <h3>Understanding Market Trends in 2024</h3>
                    <p>In 2024, market trends have shown significant shifts due to advancements in technology and changes in consumer behavior. The rise of artificial intelligence and sustainable practices are shaping the future of the market...</p>
                    <Link to="/blog/understanding-market-trends-2024">Read More</Link>
                </article>
                <article className="blog-post">
                    <h3>The Impact of AI on Market Predictions</h3>
                    <p>Artificial Intelligence is revolutionizing market predictions. With machine learning algorithms and big data analytics, businesses are now able to forecast market trends with unprecedented accuracy...</p>
                    <Link to="/blog/impact-of-ai-on-market-predictions">Read More</Link>
                </article>
                {/* Add more blog posts as needed */}
            </div>
        </div>
    );
}

export default Home;
